//
// Finally, pass the token on connect as below. Or remove it
// from connect if you don't care about authentication.

socket.connect()

// Now that you are connected, you can join channels with a topic:
let channel = socket.channel("rooms:lobby", {})

let chatInput         = $("#chat-input")
let messagesContainer = $("#messages")

chatInput.on("keypress", event => {
  if(event.keyCode === 13){
    channel.push("new_msg", {body: chatInput.val()})
    chatInput.val("")
  }
})

    // `<br/>[${Date()}] ${payload.body}`)
channel.on("new_msg", payload => {
  messagesContainer.append(
   `<div class="media msg">
        <a class="pull-left" href="#">
          <img class="media-object" data-src="holder.js/64x64" alt="64x64" style="width: 32px; height: 32px;" src="data:image/png;base64,">
        </a>
        <div class="media-body">
            <small class="pull-right time"><i class="fa fa-clock-o"></i> ${Date()}</small>
            <h5 class="media-heading">Naimish Sakhpara</h5>
            <small class="col-lg-10">
              ${payload.body}
            </small>
        </div>
    </div>`)
})

channel.join()
  .receive("ok", resp => { console.log("Joined successfully", resp) })
  .receive("error", resp => { console.log("Unable to join", resp) })

export default socket
